import BModal from 'bootstrap-vue/es/components/modal/modal';
import GlobalModalError from '@/components/common/modal-global-error';
import GlobalModalInformation from '@/components/common/modal-global-information';
import GlobalModalWarning from '@/components/common/modal-global-warning';
import GlobalModalSuccess from '@/components/common/modal-global-success';
export default {
  created: function created() {
    var _this = this;

    this.$root.$on('bv::modal::show', function (bvEvent, modalId) {
      _this.$store.commit('SET_ERRORS', '');

      _this.$store.commit('SET_PAGE_ERRORS', '');

      _this.$store.commit('SET_DISABLE_PAGE_ERRORS', true);
    });
    this.$root.$on('bv::modal::hide', function (bvEvent, modalId) {
      _this.$store.commit('SET_ERRORS', '');

      _this.$store.commit('SET_PAGE_ERRORS', '');

      _this.$store.commit('SET_PAGE_WARNINGS', '');

      _this.$store.commit('SET_PAGE_INFO', '');

      _this.$store.commit('SET_SUCCESS', '');

      _this.$store.commit('SET_DISABLE_PAGE_ERRORS', false);
    });
  },
  components: {
    BModal: BModal,
    GlobalModalError: GlobalModalError,
    GlobalModalInformation: GlobalModalInformation,
    GlobalModalWarning: GlobalModalWarning,
    GlobalModalSuccess: GlobalModalSuccess
  },
  props: {
    id: {
      type: String
    },
    title: {
      type: String
    },
    reference: {
      type: String
    },
    error: {
      type: String
    },
    success: {
      type: String
    },
    information: {
      type: String
    },
    warning: {
      type: String
    },
    modalSuccess: {
      type: String,
      default: 'false'
    }
  },
  methods: {
    show: function show() {
      this.$root.$emit('bv::show::modal', this.id);
    },
    hide: function hide() {
      this.$root.$emit('bv::hide::modal', this.id);
    }
  }
};
